import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Car from './Car/Car'

class App extends Component {

  state = { //компонент который описывает состояние в котором находится данный обьект
    cars: [
      { name: 'Ford', year: '2018' },
      { name: 'Ferrary', year: '2016' },
      { name: 'Porshe', year: '2010' }
    ],
    pageTitle: 'React components'
  }

  changeTitleHandler = (newTitle) => {
    this.setState({
      pageTitle: newTitle
    })
  }

  handleInput = (e) => {
    console.log('changed', e.target.value)
  }




  render() {
    const divStyle = {
      'textAlign': 'center'
    }

    const cars = this.state.cars


    return (
      <div style={divStyle}>
        <h1>{this.state.pageTitle}</h1>

        <input type='text' onChange={this.handleInput} />

        <button onClick={this.changeTitleHandler.bind(this, 'Changed!')}>Change Title</button>

        <Car
          name={cars[0].name}
          year={cars[0].year}
          // 1й метод передачи параметров через bind более правильный так как жрёт меньше ресурсов
          onChangeTitle={this.changeTitleHandler.bind(this, cars[0].name)}
        />
        <Car
          name={cars[1].name}
          year={cars[1].year}
          //2й метод передачи параметров через стрелочную функцию
          onChangeTitle={() => this.changeTitleHandler(cars[1].name)}
        />
        <Car
          name={cars[2].name}
          year={cars[2].year}
          onChangeTitle={() => this.changeTitleHandler(cars[2].name)}
        />
      </div>
    );
  }
}

export default App;
