import React, { Component, Children } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  render() {
    // return (
    //   <div className="App">
    //     <h1>Hello world !</h1>
    //   </div>
    // );

    return React.createElement(
      'div',
      {
        className: 'App'
      },  //опции div,
      React.createElement(
        'h1',  //опции h1
        null,
        'Hello world !'
      ),
    )
  }
}

export default App;
